import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';
import { Container, Row } from 'react-bootstrap';
import { useEffect } from 'react';

function DateSelector() {
  // let active = 1;
  let dates = [];
  let startDate = new Date();

  const populateWeekArray = () => {
    for (let number = 0; number <= 6; number++) {
      startDate.setDate(startDate.getDate() + number);
      let startDateString = startDate.toDateString();
      dates.push(startDateString);
    }
  };

  const changeDate = (value) => {
    startDate.setDate(startDate.getDate() + value);
    // startDate = startDate.toDateString();
    console.log('date', startDate);
  };

  return (
    <>
      <Container>
        <Row>
          <ButtonToolbar>
            {/* <ButtonGroup size="md" className="me-2">
              <Button>Today</Button>
              <Button variant="secondary">Back</Button>
            </ButtonGroup> */}
            <ButtonGroup size="md" height="2rem" className="me-2">
              <Button
                onClick={() => {
                  changeDate(-1);
                }}
              >
                Today
              </Button>
              <Button variant="secondary">Back</Button>
              {dates.map((date) => (
                <Button variant="light">{date}</Button>
              ))}{' '}
              <Button variant="secondary">Forward</Button>
            </ButtonGroup>
            {/* <ButtonGroup size="md" className="me-1">
              <Button variant="secondary">Forward</Button>
            </ButtonGroup> */}
          </ButtonToolbar>
        </Row>
      </Container>
    </>
    // <Nav variant="pills" defaultActiveKey="/home">
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Today</Nav.Link>
    //   </Nav.Item>
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Back</Nav.Link>
    //   </Nav.Item>
    //   {dates.map((date) => (
    //     <Nav.Item>
    //       <Nav.Link href={`/meals/${date}`}>{date}</Nav.Link>
    //     </Nav.Item>
    //   ))}
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Forward</Nav.Link>
    //   </Nav.Item>
    // </Nav>
  );
}

export default DateSelector;
