import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function List(props) {
  let items = [1, 2];
  let setItems = function () {};

  function addItem(event, i) {
    event.preventDefault();
    const itemsCopy = items.slice();
    itemsCopy.splice(i + 1, 0, '');
    setItems(itemsCopy);
  }

  function deleteItem(event, i) {
    event.preventDefault();
    const itemsCopy = items.slice();
    if (items.length > 1) {
      itemsCopy.splice(i, 1);
      setItems(itemsCopy);
    }
  }

  function handleChange(event, index) {
    event.preventDefault();
    const itemsCopy = items.slice();
    itemsCopy[index] = event.target.value;
    setItems(itemsCopy);
  }

  return (
    <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>{props.title}</Form.Label>
      <ol>
        {items.map((item, i) => {
          return (
            <Container>
              <li key={i} className="list-item">
                {props.title === 'Directions' && (
                  <div className="list-number">Step {i + 1}</div>
                )}
                {props.title === 'Ingredients' && (
                  <div className="list-number">Item {i + 1}</div>
                )}

                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Cook Time</Form.Label>
                  <Form.Control type="text" placeholder="Enter Cook Time" />
                </Form.Group>
                <div className="list-buttons">
                  <Button
                    className="list-button"
                    onClick={(e) => {
                      deleteItem(e, i);
                    }}
                  >
                    -
                  </Button>
                  <Button
                    className="list-Button"
                    onClick={(e) => {
                      addItem(e, i);
                    }}
                  >
                    +
                  </Button>
                </div>
              </li>
            </Container>
          );
        })}
      </ol>
    </Form.Group>
  );
}

export default List;
