import Pagination from 'react-bootstrap/Pagination';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React, { useEffect, useState, useContext } from 'react';
import MealCard from './MealCard';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import DateSelector from './DateSelector';
import AddMealCard from './AddMealCard';
import Accordion from 'react-bootstrap/Accordion';
import { DetailsContext } from './App';

function MealPlanner() {
  const { user, group, meals, setMeals } = useContext(DetailsContext);
  let dates = [];
  for (let number = 0; number <= 6; number++) {
    let date = new Date();
    date.setDate(date.getDate() + number);
    date = date.toDateString();

    dates.push(date);
  }

  return (
    <div>
      <Container fluid>
        <Row className="mb-3">
          <DateSelector></DateSelector>
        </Row>
        <Tabs
          defaultActiveKey="all"
          id="uncontrolled-tab-example"
          className="mb-3"
        >
          <Tab eventKey="all" title="All">
            <Accordion>
              {' '}
              {dates.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
          <Tab eventKey="breakfast" title="Breakfast">
            <Accordion>
              {' '}
              {dates.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
          <Tab eventKey="lunch" title="Lunch">
            <Accordion>
              {' '}
              {dates.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
          <Tab eventKey="dinner" title="Dinner">
            <Accordion>
              {' '}
              {dates.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
        </Tabs>
      </Container>
      <div>
        <br />

        <br />
      </div>
    </div>
  );
}

export default MealPlanner;
