    try {
      await submitLoginUser({
        user: username,
        password,
      });
      // localStorage.setItem('loggedIn', true);
      // window.dispatchEvent(new Event('loggedIn'));
      // navigate('/redirect');

      getUser();
      // navigate('/');
      props.setOptionSelection('options');
    } catch (error) {
      // do something with error(s)
    }
  }

  const getUser = async (body) => {
    const response = await fetch('/api/users/user');
    const json = await response.json();
    // console.log('getuser', json);
    // json.user - username
    // json.group.groupName - groupName
    localStorage.setItem('user', json.user);
    await setUser(json.user);
    const saveGroup = (json) => {
      localStorage.setItem('group', json.group.groupName);
      setGroup(json.group.groupName);
    };
    {
      json.group && saveGroup(json);
    }

    return json.user;
  };
  return (
    <Container>
      <h2 className="mb-3">Login</h2>
      <Form onSubmit={loginUser}>
        <FloatingLabel
          controlId="floatingInput"
          label="Email address"
          className="mb-3"
        >
          <Form.Control type="email" placeholder="name@example.com" />
        </FloatingLabel>
        <FloatingLabel
          controlId="floatingPassword"
          label="Password"
          className="mb-3"
        >
          <Form.Control type="password" placeholder="Password" />
        </FloatingLabel>

        <Button variant="primary" type="submit" onClick={handleSubmit}>
          Submit
        </Button>
      </Form>
    </Container>
  );
}

export default Login;
