import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';

function DateSelector() {
  // let active = 1;
  let dates = [];
  for (let number = 0; number <= 6; number++) {
    let date = new Date();
    date.setDate(date.getDate() + number);
    date = date.toDateString();

    dates.push(date);
  }

  return (
    <>

      <ButtonToolbar>
        <ButtonGroup size="lg" className="me-2">
          <Button>Today</Button>
          <Button variant="secondary">Back</Button>
        </ButtonGroup>
        <ButtonGroup size="lg" className="me-2">
          {/* <Button>Today</Button> */}
          {/* <Button variant="secondary">Back</Button> */}
          {dates.map((date) => (
            <Button>{date}</Button>
          ))}{' '}
          {/* <Button variant="secondary">Forward</Button> */}
        </ButtonGroup>
        <ButtonGroup size="lg" className="me-2">
          <Button variant="secondary">Forward</Button>
        </ButtonGroup>
      </ButtonToolbar>
    </>
    // <Nav variant="pills" defaultActiveKey="/home">
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Today</Nav.Link>
    //   </Nav.Item>
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Back</Nav.Link>
    //   </Nav.Item>
    //   {dates.map((date) => (
    //     <Nav.Item>
    //       <Nav.Link href={`/meals/${date}`}>{date}</Nav.Link>
    //     </Nav.Item>
    //   ))}
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Forward</Nav.Link>
    //   </Nav.Item>
    // </Nav>
  );
}

export default DateSelector;
