import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function List(props) {
  let items = [];
  let setItems = function () {};

  function addItem(event, i) {
    event.preventDefault();
    const itemsCopy = items.slice();
    itemsCopy.splice(i + 1, 0, '');
    setItems(itemsCopy);
  }

  function deleteItem(event, i) {
    event.preventDefault();
    const itemsCopy = items.slice();
    if (items.length > 1) {
      itemsCopy.splice(i, 1);
      setItems(itemsCopy);
    }
  }

  function handleChange(event, index) {
    event.preventDefault();
    const itemsCopy = items.slice();
    itemsCopy[index] = event.target.value;
    setItems(itemsCopy);
  }

  return (
    <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>{props.title}</Form.Label>
      <ol>
        {items.map((item, i) => {
          return (
            <li key={i} className="list-item">
              {title === 'Directions' && (
                <div className="list-number">Step {i + 1}</div>
              )}
              {title === 'Ingredients' && (
                <div className="list-number">Item {i + 1}</div>
              )}

              <input
                className="form-input"
                name="directions"
                type="text"
                value={item}
                onChange={(e) => {
                  handleChange(e, i);
                }}
              />
              <div className="list-buttons">
                <button
                  className="list-button"
                  onClick={(e) => {
                    deleteItem(e, i);
                  }}
                >
                  -
                </button>
                <button
                  className="list-button"
                  onClick={(e) => {
                    addItem(e, i);
                  }}
                >
                  +
                </button>
              </div>
            </li>
          );
        })}
      </ol>
    </Form.Group>
  );
}

export default List;
