    <>
      <Container className="DateSelector" fluid>
        <Row>
          <ButtonToolbar>
            <ButtonGroup size="lg" className="me-2">
              <Button
                className="date-button"
                onClick={() => {
                  props.setSelection(0);
                }}
              >
                Today
              </Button>
              <Button
                className="date-button d-xs-none"
                variant="secondary"
                onClick={() => {
                  props.setSelection(props.selection - 1);
                }}
              >
                Back
              </Button>
              {props.dateArray.map((date, index) => (
                <Button
                  variant="light"
                  className="date-button"
                  onClick={() => {
                    props.setSelection(props.selection + index);
                  }}
                >
                  {date}
                </Button>
              ))}{' '}
              <Button
                variant="secondary"
                className="date-button"
                onClick={() => {
                  props.setSelection(props.selection + 1);
                }}
              >
                Forward
              </Button>
            </ButtonGroup>
            {/* <ButtonGroup size="md" className="me-1">
              <Button variant="secondary">Forward</Button>
            </ButtonGroup> */}
