import Pagination from 'react-bootstrap/Pagination';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React, { useEffect, useState, useContext } from 'react';
import MealCard from './MealCard';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import DateSelector from './DateSelector';
import AddMealCard from './AddMealCard';
import Accordion from 'react-bootstrap/Accordion';
import { DetailsContext } from '../App';
import './MealPlanner.css';
import moment from 'moment';
moment().format();

function MealPlanner() {
  const { user, group, meals, setMeals } = useContext(DetailsContext);
  var startDate = moment();
  const [dateArray, setDateArray] = useState([]);
  const [selection, setSelection] = useState(0);

  const getDateArray = () => {
    const array = [];
    for (let i = 0; i < 7; i++) {
      startDate = moment().add(i + selection, 'd');
      array.push(startDate.format('MMM D YY'));
    }
    setDateArray(array);
  };

  const getMeals = async (body) => {
    const response = await fetch('/api/meals/getMeals', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body),
    });
    const json = await response.json();
  };

  useEffect(() => {
    getDateArray();
  }, [selection]);
  useEffect(() => {
    getMeals();
  }, []);

  return (
    <div>
      <Container fluid>
        <Row className="mb-3">
          <DateSelector
            // className="DateSelector"
            // style={{ height: 400 }}
            dateArray={dateArray}
            setSelection={setSelection}
            selection={selection}
          ></DateSelector>
        </Row>
        <Tabs
          defaultActiveKey="all"
          id="uncontrolled-tab-example"
          className="mb-3"
        >
          <Tab eventKey="all" title="All">
            <Accordion>
              {dateArray.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
          <Tab eventKey="breakfast" title="Breakfast">
            <Accordion>
              {' '}
              {dateArray.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
          <Tab eventKey="lunch" title="Lunch">
            <Accordion>
              {' '}
              {dateArray.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
          <Tab eventKey="dinner" title="Dinner">
            <Accordion>
              {' '}
              {dateArray.map((date) => {
                return (
                  <Accordion.Item>
                    <Accordion.Header>{date}</Accordion.Header>
                    <Accordion.Body>
                      <Row>
                        {' '}
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                        <Col>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <MealCard></MealCard>
                          <AddMealCard></AddMealCard>
                        </Col>
                      </Row>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Tab>
        </Tabs>
      </Container>
      <div>
        <br />

        <br />
      </div>
    </div>
  );
}

export default MealPlanner;
