import Nav from 'react-bootstrap/Nav';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';
import { Container, Row } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import moment from 'moment';
moment().format();

function DateSelector(props) {
  // let active = 1;
  var startDate = moment();
  const [dateArray, setDateArray] = useState([]);
  const [selection, setSelection] = useState(0);
  console.log('start', startDate);
  // let startDate = new Date();
  // let selection = 0;
  // var getDateArray = function (startDate) {
  //   console.log('getDateArray', startDate);
  //   var array = [];
  //   // let date = startDate;
  //   for (let i = selection; i < 7; i++) {
  //     startDate = moment().add(i, 'd');
  //     array.push(startDate.format('MMM D YY'));
  //     // date.setDate(date.getDate() + 1);
  //   }
  //   setDateArray(array);
  // };

  const getArray = () => {
    const array = [];
    for (let i = 0; i < 7; i++) {
      startDate = moment().add(i + selection, 'd');
      array.push(startDate.format('MMM D YY'));
    }
    setDateArray(array);
  };

  useEffect(() => {
    getArray();
  }, [selection]);

  return (
    <>
      <Container>
        <Row>
          <ButtonToolbar>
            {/* <ButtonGroup size="md" className="me-2">
              <Button>Today</Button>
              <Button variant="secondary">Back</Button>
            </ButtonGroup> */}
            <ButtonGroup size="md" height="2rem" className="me-2">
              <Button
                onClick={() => {
                  setSelection(0);
                }}
              >
                Today
              </Button>
              <Button
                variant="secondary"
                onClick={() => {
                  setSelection(selection - 1);
                }}
              >
                Back
              </Button>
              {dateArray.map((date) => (
                <Button variant="light">{date}</Button>
              ))}{' '}
              <Button
                variant="secondary"
                onClick={() => {
                  setSelection(selection + 1);
                }}
              >
                Forward
              </Button>
            </ButtonGroup>
            {/* <ButtonGroup size="md" className="me-1">
              <Button variant="secondary">Forward</Button>
            </ButtonGroup> */}
          </ButtonToolbar>
        </Row>
      </Container>
    </>
    // <Nav variant="pills" defaultActiveKey="/home">
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Today</Nav.Link>
    //   </Nav.Item>
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Back</Nav.Link>
    //   </Nav.Item>
    //   {dates.map((date) => (
    //     <Nav.Item>
    //       <Nav.Link href={`/meals/${date}`}>{date}</Nav.Link>
    //     </Nav.Item>
    //   ))}
    //   <Nav.Item>
    //     <Nav.Link href="/meals/today">Forward</Nav.Link>
    //   </Nav.Item>
    // </Nav>
  );
}

export default DateSelector;
