import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function List(props) {
  let items = [1, 2];
  let setItems = function () {};

  function addItem(event, i) {
    event.preventDefault();
    const itemsCopy = items.slice();
    itemsCopy.splice(i + 1, 0, '');
    setItems(itemsCopy);
  }

  function deleteItem(event, i) {
    event.preventDefault();
    const itemsCopy = items.slice();
    if (items.length > 1) {
      itemsCopy.splice(i, 1);
      setItems(itemsCopy);
    }
  }

  function handleChange(event, index) {
    event.preventDefault();
    const itemsCopy = items.slice();
    itemsCopy[index] = event.target.value;
    setItems(itemsCopy);
  }

  return (
    <Container>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Row>
          <Form.Label>{props.title}</Form.Label>
        </Row>

        <Row></Row>

        <ol>
          {items.map((item, i) => {
            return (
              <Row>
                <Col>
                  {props.title === 'Directions' && (
                    <div className="list-number">Step {i + 1}</div>
                  )}
                  {props.title === 'Ingredients' && (
                    <div className="list-number">Item {i + 1}</div>
                  )}
                </Col>
                <li key={i} className="list-item">
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Control type="text" placeholder="Enter details" />
                  </Form.Group>
                  <div className="list-buttons">
                    <Button
                      className="list-button"
                      onClick={(e) => {
                        deleteItem(e, i);
                      }}
                    >
                      -
                    </Button>
                    <Button
                      className="list-button"
                      onClick={(e) => {
                        addItem(e, i);
                      }}
                    >
                      +
                    </Button>
                  </div>
                </li>
              </Row>
            );
          })}
        </ol>
      </Form.Group>
    </Container>
  );
}

export default List;
